mapper.select:
SELECT * 
FROM user
WHERE id>0

AND username LIKE CONCAT ('%',#{param.username},'%')


AND phone LIKE CONCAT ('%',#{param.phone},'%') 


AND email LIKE CONCAT ('%',#{param.email},'%') 


AND gender = #{param.gender}


AND createtime = #{param.createTime}


AND updatetime = #{param.updateTime}
